      d[y][s]=d[x][t]+cc*t;
      que.push(PP(d[y][s],P(y,s)));
    }
  }
  ll ans=1LL<<59;
  for(int i=0; i<C; i++) ans=min(ans,d[0][i]);
  cout << ans << endl;
  return 0;
}

