#include<list>
#include<queue>
#include<deque>
#include<algorithm>
#include<numeric>
#include<utility>
#include<complex>
#include<functional>

using namespace std;

/* constant */

const int MAX_N = 500;
const int MAX_C = 50;
const long long LINF = 1LL << 61;

/* typedef */

typedef long long ll;
typedef pair<int,int> pii;
typedef vector<pii> vpii;

struct Stat {
  ll d;
  int i, v;
  Stat() {}
  Stat(ll _d, int _i, int _v): d(_d), i(_i), v(_v) {}

  bool operator<(const Stat &s) const { return d > s.d; }
};

/* global variables */

vpii nbrs[MAX_N];
ll ds0[MAX_N][MAX_C], ds1[MAX_N][MAX_C];

/* subroutines */

/* main */

int main() {
  int n, m;
  scanf("%d%d", &n, &m);

  for (int i = 0; i < m; i++) {
    int x, y, t;
    scanf("%d%d%d", &x, &y, &t);
    x--, y--;
    nbrs[x].push_back(pii(y, t));
    nbrs[y].push_back(pii(x, t));
  }

  int v0, a, b, c;
  scanf("%d%d%d%d", &v0, &a, &b, &c);

  for (int i = 0; i < n; i++) {
    fill(ds0[i], ds0[i] + c, LINF);
    fill(ds1[i], ds1[i] + c, LINF);
  }

  int gl = n - 1;
  ds0[0][v0] = 0;
  priority_queue<Stat> q;
  q.push(Stat(0, 0, v0));

  while (! q.empty()) {
    Stat u = q.top(); q.pop();
    if (u.d != ds0[u.i][u.v]) continue;
    if (u.i == gl) continue;

    int vv = ((ll)a * u.v + b) % c;
    vpii &nbru = nbrs[u.i];
    for (vpii::iterator vit = nbru.begin(); vit != nbru.end(); vit++) {
      int &vi = vit->first;
      ll vd = u.d + (ll)u.v * vit->second;
      if (ds0[vi][vv] > vd) {
	ds0[vi][vv] = vd;
	q.push(Stat(vd, vi, vv));
      }
    }
  }
  //for (int j = 0; j < c; j++) printf("%lld ", ds0[gl][j]); putchar('\n');

  for (int j = 0; j < c; j++)
    if (ds0[gl][j] < LINF) {
      ds1[gl][j] = ds0[gl][j];
      q.push(Stat(ds0[gl][j], gl, j));
    }

  ll mind = LINF;
  while (! q.empty()) {
    Stat u = q.top(); q.pop();
    if (u.d != ds1[u.i][u.v]) continue;
    if (u.i == 0) {
      mind = u.d;
      break;
    }

    int vv = ((ll)a * u.v + b) % c;
    vpii &nbru = nbrs[u.i];
    for (vpii::iterator vit = nbru.begin(); vit != nbru.end(); vit++) {
      int &vi = vit->first;
      ll vd = u.d + (ll)u.v * vit->second;
      if (ds1[vi][vv] > vd) {
	ds1[vi][vv] = vd;
	q.push(Stat(vd, vi, vv));
      }
    }
  }

  printf("%lld\n", mind);
  return 0;
}

#include <stdio.h>
#include <cmath>
#include <algorithm>
#include <cfloat>
#include <stack>
#include <queue>
#include <vector>
#include <string>
#include <iostream>
#include <set>
#include <map>
typedef long long int ll;
typedef unsigned long long int ull;
#define BIG_NUM 2000000000
#define MOD 1000000007
#define EPS 0.000000001
using namespace std;

enum Type{
	not_visited,
	visited,
};

struct Info{
	Info(int arg_node_id,ll arg_sum_dist,ll arg_v,Type arg_type){
		node_id = arg_node_id;
		sum_dist = arg_sum_dist;
		v = arg_v;
		type = arg_type;
	}
	bool operator<(const struct Info &arg) const{
		return sum_dist > arg.sum_dist;
	};
	int node_id;
	ll sum_dist,v;
	Type type;
};

struct Data{
	Data(int arg_to,ll arg_time){
		to = arg_to;
		time = arg_time;
	}
	int to;
	ll time;
};

int N,M;
ll A,B,C;
ll min_dist[2][500][50];

ll calc_V(ll pre_v){
	return (A*pre_v+B)%C;
}

int main(){

	scanf("%d %d",&N,&M);

	vector<Data> V[N];

	int from,to;
	ll time;

	for(int loop = 0; loop < M; loop++){
		scanf("%d %d %lld",&from,&to,&time);
		from--;
		to--;
		V[from].push_back(Data(to,time));
		V[to].push_back(Data(from,time));
	}

	ll first_v;
	scanf("%lld",&first_v);
	scanf("%lld %lld %lld",&A,&B,&C);

	for(int a = 0; a < 2; a++){
		for(int b = 0; b < N; b++){
			for(int c = 0; c < 50; c++)min_dist[a][b][c] = 999999999999999;
		}
	}

	priority_queue<Info> Q;
	min_dist[not_visited][0][first_v] = 0;
	Q.push(Info(0,0,first_v,not_visited));

	ll ans = 999999999999999;
	int next_node;
	ll next_v;

	while(!Q.empty()){

		if(Q.top().node_id == 0 && Q.top().type == visited){
			ans = min(ans,Q.top().sum_dist);
			Q.pop();
		}else if(Q.top().sum_dist > min_dist[Q.top().type][Q.top().node_id][Q.top().v]){
			Q.pop();
		}else{
			next_v = calc_V(Q.top().v);

			for(int i = 0; i < V[Q.top().node_id].size(); i++){
				next_node = V[Q.top().node_id][i].to;

				if(next_node != N-1){
					if(min_dist[Q.top().type][next_node][next_v] > Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time){
						min_dist[Q.top().type][next_node][next_v] = Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time;
						Q.push(Info(next_node,min_dist[Q.top().type][next_node][next_v],next_v,Q.top().type));
					}
				}else{ //next_node == N-1
					if(min_dist[visited][next_node][next_v] > Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time){
						min_dist[visited][next_node][next_v] = Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time;
						Q.push(Info(next_node,min_dist[visited][next_node][next_v],next_v,visited));
					}
				}
			}
			Q.pop();
		}
	}

	printf("%lld\n",ans);

	return 0;
}

