#include <bits/stdc++.h>
using namespace std;

#define rep(i, n) for(int i=0; i<(int)(n); i++)
typedef long long ll;
const ll INFLL = 1e18;

struct Edge {
    int to; ll t;
    Edge(int _to, ll _t) : to(_to), t(_t) {}
};

struct State {
    int id, v; ll d;
    State(int _id, int _v, ll _d) : id(_id), v(_v), d(_d) {}
    bool operator < (const State &b) const {return d < b.d;}
    bool operator > (const State &b) const {return d > b.d;}
};

typedef pair<int, int> PII;

vector<vector<Edge>> G;

int main()
{
    int N, M;
    cin >> N >> M;

    G.resize(N);

    rep(i, M) {
        int X, Y, T;
        cin >> X >> Y >> T;
        X--, Y--;
        G[X].push_back(Edge(Y, T));
        G[Y].push_back(Edge(X, T));
    }

    int V0, A, B, C;
    cin >> V0 >> A >> B >> C;

    priority_queue<State, vector<State>, greater<State>> Q, Q2;
    map<PII, ll> d, d2;

    {
        d[PII(0, V0)] = 0LL;
        Q.push({0, V0, 0LL});

        while (Q.size()) {
            State s = Q.top(); Q.pop();
            // cerr << "(id, v) = (" << s.id << ", " << s.v << ")" << endl;
            if (s.id == N-1) {
                Q2.push(s);
                d2[PII(s.id, s.v)] = s.d;
            }

            if (d.count(PII(s.id, s.v)) && d[PII(s.id, s.v)] < s.d) continue;
            for (auto e : G[s.id]) {
                ll ncost = s.d + s.v * e.t;
                int nv = (A * s.v + B) % C;

                if (!d.count(PII(e.to, nv)) || d[PII(e.to, nv)] > ncost) {
                    d[PII(e.to, nv)] = ncost;
                    Q.push(State(e.to, nv, ncost));
                }
            }
        }
    }

    // cerr << "-------------------" << endl;

    ll ans = INFLL;
    {
        while (Q2.size()) {
            State s = Q2.top(); Q2.pop();
            // cerr << "(id, v) = (" << s.id << ", " << s.v << ")" << endl;

            if (s.id == 0) {
                ans = s.d;
                break;
            }

            if (d2.count(PII(s.id, s.v)) && d2[PII(s.id, s.v)] < s.d) continue;
            for (auto e : G[s.id]) {
                ll ncost = s.d + s.v * e.t;
                int nv = (A * s.v + B) % C;

                if (!d2.count(PII(e.to, nv)) || d2[PII(e.to, nv)] > ncost) {
                    d2[PII(e.to, nv)] = ncost;
                    Q2.push(State(e.to, nv, ncost));
                }
            }
        }
    }

    cout << ans << endl;
}

