#include <bits/stdc++.h>
#define N 501
using namespace std;
typedef long long ll;
typedef pair<ll,ll> P;
typedef pair<ll,P> PP;
ll const INF = 1LL<<55;
ll n,m;
vector<P> G[N];
ll v0,a,b,C;
ll v[5010];

int nv(ll v){return (v*a+b)%C;}

ll dijkstra(ll start,ll goal,ll c){
  ll D[500][100];
  for(ll i=0;i<500;i++)
    for(ll j=0;j<100;j++) D[i][j] = INF;

  priority_queue<PP,vector<PP>,greater<PP> > Q;
  Q.push(PP(0,P(start,c)));
  D[start][c] = 0;
  ll res = INF;
  while(!Q.empty()){
    PP t = Q.top();Q.pop();
    ll cost = t.first; 
    ll pos = t.second.first;
    ll v = t.second.second;
    if(D[pos][v]<cost) continue;
    if(pos==goal&&goal == 0)return cost; 
    if(pos==goal&&goal == n-1){
      res=min(res,cost+dijkstra(n-1,0,v));
      continue;
    }

    for(ll i=0;i<(ll)G[pos].size();i++){
      ll npos = G[pos][i].first;
      ll ncost= cost+v*G[pos][i].second;
      ll nV = nv(v); 
     if(D[npos][nV]<=ncost)continue;
      Q.push(PP(ncost,P(npos,nV)));
      D[npos][nV] = ncost;
    }
  }
  return res;
}

int main(){
  cin>>n>>m;
  for(ll i=0,x,y,t;i<m;i++){
    cin>>x>>y>>t;
    x--,y--;
    G[x].push_back(P(y,t));
    G[y].push_back(P(x,t));
  }

  int v0;
  cin>>v0>>a>>b>>C;
  cout<<dijkstra(0,n-1,v0)<<endl;
  return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef long long ll;

int N,M;
int A, B, C;
struct edge {
  int to;
  ll t;
  edge(){

  }
  edge(int to,ll t):to(to),t(t){}
};
struct state{
  int id,v,f;
  ll c;
  state(){

  }
  state(int id,int v,int f,ll c):id(id),v(v),f(f),c(c){}
  bool operator<(const state &st) const {
    return c > st.c;
  }
};

ll H[555][55][2];
const ll INF = (ll)(1<<29)*(ll)(1<<29);
vector<edge> G[555];

ll dijk(int v0){
  priority_queue<state> q;
  fill( H[0][0], H[553][51], INF );
  q.push( state(0,v0,0,0) );
  H[0][v0][0] = 0LL;
  while( !q.empty() ){
    state p = q.top(); q.pop();
    if( H[p.id][p.v][p.f] < p.c ) continue;
    for( edge e : G[p.id] ){
      int nv = (A * p.v + B)%C;

      int nf = p.f; if( e.to == N-1 ) nf = 1;
      ll nc = (ll)p.v * e.t + p.c;
      if( H[e.to][nv][nf] > nc ){
        H[e.to][nv][nf] = nc;
        q.push( state( e.to, nv, nf, nc ) );
      }
    }
  }
  ll res = INF;
  for(int i=0;i<=C;i++)
    res = min( res, H[0][i][1] );
  return res;
}

int main(){
  cin >> N >> M;
  for(int i=0;i<M;i++){
    int x,y; ll t;
    cin >> x >> y >> t; --x; --y;
    G[x].emplace_back( y, t );
    G[y].emplace_back( x, t );
  }
  int v0; cin >> v0;
  cin >> A >> B >> C;
  cout << dijk(v0) << endl;
}

