void add_edge(int time,int from,int to,int cost){
  G[time][from].push_back(edge((time+1)%c,to,cost));
  G[time][to].push_back(edge((time+1)%c,from,cost));
}
void add_edge2(int time,int from,int to,int cost){
  G[time][from].push_back(edge(time,to,cost));
  G[time][to].push_back(edge(time,from,cost));
}
struct st{
  int dist,time,pos;
  st(){}
  st(int dist,int time,int pos):dist(dist),time(time),pos(pos){}
  bool operator<(const st& a) const{
    return dist>a.dist;
  }
};
signed main(){
  int n,m;
  cin>>n>>m;
  int x[m],y[m],t[m];
  for(int i=0;i<m;i++) cin>>x[i]>>y[i]>>t[i];
  cin>>v>>a>>b>>c;

  for(int i=0;i<m;i++){
    x[i]--;y[i]--;
    //cout<<i<<":"<<endl;
    int ve=v;
    for(int j=0;j<c;j++){
      //cout<<ve<<endl;
      add_edge(j,x[i],y[i],ve*t[i]);
      add_edge(j,x[i]+n,y[i]+n,ve*t[i]);
      ve=(a*ve+b)%c;
    }
  }
  for(int j=0;j<c;j++) add_edge2(j,n-1,n+n-1,0);


  memset(dp,-1,sizeof(dp));

  int ans=1LL<<50LL;
  priority_queue<st> q;
  q.push(st(0,0,0));
  dp[0][0]=0;
  while(!q.empty()){
    st p=q.top();q.pop();
    int t=p.time,u=p.pos,d=p.dist;
    assert(0<=t&&t<c);
    //cout<<t<<" "<<u<<" "<<d<<endl;
    if(u==n){
      ans=d;
      break;
    }
    if(dp[t][u]<d) continue;
    assert(dp[t][u]==d);
    for(int i=0;i<(int)G[t][u].size();i++){
      int nt=G[t][u][i].time,nu=G[t][u][i].to,nc=G[t][u][i].cost;
      if(dp[nt][nu]<0||d+nc<dp[nt][nu]){
	dp[nt][nu]=d+nc;
	q.push(st(d+nc,nt,nu));
      }
    }
  }
  assert(ans>=0);
  cout<<ans<<endl;
  return 0;
}

