using namespace std;

enum Type{
	not_visited,
	visited,
};

struct Info{
	Info(int arg_node_id,ll arg_sum_dist,ll arg_v,Type arg_type){
		node_id = arg_node_id;
		sum_dist = arg_sum_dist;
		v = arg_v;
		type = arg_type;
	}
	bool operator<(const struct Info &arg) const{
		return sum_dist > arg.sum_dist;
	};
	int node_id;
	ll sum_dist,v;
	Type type;
};

struct Data{
	Data(int arg_to,ll arg_time){
		to = arg_to;
		time = arg_time;
	}
	int to;
	ll time;
};

int N,M;
ll A,B,C;
ll min_dist[2][500][50];

ll calc_V(ll pre_v){
	return (A*pre_v+B)%C;
}

int main(){

	scanf("%d %d",&N,&M);

	vector<Data> V[N];

	int from,to;
	ll time;

	for(int loop = 0; loop < M; loop++){
		scanf("%d %d %lld",&from,&to,&time);
		from--;
		to--;
		V[from].push_back(Data(to,time));
		V[to].push_back(Data(from,time));
	}

	ll first_v;
	scanf("%lld",&first_v);
	scanf("%lld %lld %lld",&A,&B,&C);

	for(int a = 0; a < 2; a++){
		for(int b = 0; b < N; b++){
			for(int c = 0; c < 50; c++)min_dist[a][b][c] = 999999999999999;
		}
	}

	priority_queue<Info> Q;
	min_dist[not_visited][0][first_v] = 0;
	Q.push(Info(0,0,first_v,not_visited));

	ll ans = 999999999999999;
	int next_node;
	ll next_v;

	while(!Q.empty()){

		if(Q.top().node_id == 0 && Q.top().type == visited){
			ans = min(ans,Q.top().sum_dist);
			Q.pop();
		}else if(Q.top().sum_dist > min_dist[Q.top().type][Q.top().node_id][Q.top().v] || Q.top().sum_dist >= ans){
			Q.pop();
		}else{
			next_v = calc_V(Q.top().v);

			for(int i = 0; i < V[Q.top().node_id].size(); i++){
				next_node = V[Q.top().node_id][i].to;

				if(next_node != N-1){
					if(min_dist[Q.top().type][next_node][next_v] > Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time){
						min_dist[Q.top().type][next_node][next_v] = Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time;
						Q.push(Info(next_node,min_dist[Q.top().type][next_node][next_v],next_v,Q.top().type));
					}
				}else{ //next_node == N-1
					if(min_dist[visited][next_node][next_v] > Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time){
						min_dist[visited][next_node][next_v] = Q.top().sum_dist+Q.top().v*V[Q.top().node_id][i].time;
						Q.push(Info(next_node,min_dist[visited][next_node][next_v],next_v,visited));
					}
				}
			}
			Q.pop();
		}
	}

	printf("%lld\n",ans);

	return 0;
}

