  for(ll i=1;;i++){
    v[i] = (a*v[i-1]+b)%C;
    if(S.count(v[i]))break;
    S.insert(v[i]);
  }
  nC=S.size();
  cout<<dijkstra(0,n-1,0)<<endl;
  return 0;
}

