	dp[1][v0]=0;
	qu.push({1,v0,0});
	while(qu.size()){
		Trio u=qu.top(); qu.pop();
		if(u.to==n+1){
			cout<<dp[u.to][u.v]<<endl;
			return 0;
		}
		if(dp[u.to][u.v] < u.cost)continue;
		for(auto e:G[u.to][u.v]){
			if(dp[e.to][e.v] > dp[u.to][u.v] + e.cost){
				dp[e.to][e.v] = dp[u.to][u.v] + e.cost;
				qu.push({e.to,e.v,dp[e.to][e.v]});
			}
		}
	}
}

