                dist[to][n_v][n_f] = n_cost;
                que.emplace(to, n_v, n_f, n_cost);
            }
        }
    }

    ll ans = LONGINF;
    for(int i=0; i<C; i++) chmin(ans, dist[0][i][1]);
    printf("%lld\n", ans);
    return 0;
}

