    for(int j=0;j<c;j++){
      //cout << ch(lx[i],j)<<endl;
      G[ch(lx[i],j)].push_back(edge(ch(ly[i],(a*j+b)%c),j*lt[i]));
      G[ch(ly[i],j)].push_back(edge(ch(lx[i],(a*j+b)%c),j*lt[i]));      
      G[VV+ch(lx[i],j)].push_back(edge(VV+ch(ly[i],(a*j+b)%c),j*lt[i]));
      G[VV+ch(ly[i],j)].push_back(edge(VV+ch(lx[i],(a*j+b)%c),j*lt[i]));      
    }
  }
  for(int i=0;i<c;++i)
    G[ch(n,i)].push_back(edge(VV+ch(n,i),0));      

  //cout << 1<<endl;
  long long ans=INF;
  long long sd[V];
  dk(ch(1,v),sd);

  for(int i=0;i<c;i++){
    ans = min(ans, sd[VV+ch(1,i)]);
  }
  //cout << 3<<endl;

  cout << ans << endl;
  return 0;
}
void dk(long long s,long long* d){
  priority_queue<P,vector<P>,greater<P> >que;
  fill(d,d+V,INF);
  d[s]=0;
  que.push(P(0,s));

  while(!que.empty()){
    P p = que.top();que.pop();
    int v = p.second;
    if(d[v]<p.first) continue;
    for(int i=0;i<G[v].size();i++){
      edge e = G[v][i];
      if(d[e.to] > d[v]+e.cost){
	d[e.to]=d[v]+e.cost;
	que.push(P(d[e.to],e.to));
      }
    }
  }
}
int ch(long long A,long long B){
  return A*c+B;
}

