using namespace std;

typedef pair<int,int> P;
typedef pair<P,P> P2;

int n, m, d[C][N][2], v0, a, b, c;

vector<P> G[N];

priority_queue<P2,vector<P2>,greater<P2> > q;

int dijkstra(){

  for(int i=0;i<C;i++)
    for(int j=0;j<N;j++)
      for(int k=0;k<2;k++) d[i][j][k]=INF;

  d[v0][0][0]=0;

  q.push(P2(P(0,v0),P(0,0)));

  while(!q.empty()){

    P2 t=q.top(); q.pop();

    int cost=t.first.first;

    int v=t.first.second;

    int node=t.second.first;

    int idx=t.second.second;

    if(d[v][node][idx]<cost) continue;

    if(idx&&node==0) return cost;

    if(!idx&&node==n-1){

      if(d[v][node][1]>cost){

	d[v][node][1]=cost;

	q.push(P2(P(cost,v),P(node,1)));

      }

    }

    for(int i=0;i<G[node].size();i++){

      int nnode=G[node][i].first;

      int nv=(a*v+b)%c;

      int ncost=cost+v*G[node][i].second;

      if(d[nv][nnode][idx]>ncost){

	d[nv][nnode][idx]=ncost;

	q.push(P2(P(ncost,nv),P(nnode,idx)));

      }

    }

  }

}

