using namespace std;
typedef long long int lli;
typedef pair<lli, lli> pll;
const lli inf = 1e18;

struct info{
    int p;
    lli v;
    lli d;
    info(int p, lli v, lli d):p(p),v(v),d(d){}
    info(){}
    bool operator <(const info &a) const{
        return d > a.d;
    }
};

int main(){
    int n,m;
    cin >> n >> m;
    vector<vector<pll>> adj(n);
    for(int i=0; i<m; i++){
        lli x,y,t;
        cin >> x >> y >> t;
        x--; y--;
        adj[x].emplace_back(y, t);
        adj[y].emplace_back(x, t);
    }
    lli v_init,a,b,c;
    cin >> v_init >> a >> b >> c;

    priority_queue<info> wait;
    wait.push(info(0, v_init, 0));
    vector<vector<lli>> mincost(n, vector<lli>(c, inf));
    mincost[0][v_init] = 0;
    while(!wait.empty()){
        int p = wait.top().p;
        lli v = wait.top().v;
        lli d = wait.top().d;
        wait.pop();
        if(d > mincost[p][v]) continue;
        for(auto next: adj[p]){
            int np = next.first;
            lli t = next.second;
            lli nd = d + v*t;
            lli nv = (a*v +b) %c;
            if(nd < mincost[np][nv]){
                mincost[np][nv] = nd;
                wait.push(info(np, nv, nd));
            }
        }
    }

    lli ans = inf;
    for(int i=0; i<c; i++){
        wait.push(info(n-1, i, 0));
        vector<vector<lli>> mincost_back(n, vector<lli>(c, inf));
        mincost_back[n-1][i] = 0;
        while(!wait.empty()){
            int p = wait.top().p;
            lli v = wait.top().v;
            lli d = wait.top().d;
            wait.pop();
            if(d > mincost_back[p][v]) continue;
            for(auto next: adj[p]){
                int np = next.first;
                lli t = next.second;
                lli nd = d + v*t;
                lli nv = (a*v +b) %c;
                if(nd < mincost_back[np][nv]){
                    mincost_back[np][nv] = nd;
                    wait.push(info(np, nv, nd));
                }
            }
        }
        ans = min(ans, mincost[n-1][i] + *min_element(mincost_back[0].begin(), mincost_back[0].end()));
    }
    cout << ans << endl;
    return 0;
}

