	cin >> xs[i] >> ys[i] >> ts[i];
	--xs[i];
	--ys[i];
  }

  LL V, A, B, C;
  cin >> V >> A >> B >> C;

  Graph G(500*50*2);
  REP(i,M){
	REP(k,C){
	  G[idx(xs[i], k, 0)].EB(idx(ys[i], (A*k+B)%C, 0), k*ts[i]);
	  G[idx(ys[i], k, 0)].EB(idx(xs[i], (A*k+B)%C, 0), k*ts[i]);
	  G[idx(xs[i], k, 1)].EB(idx(ys[i], (A*k+B)%C, 1), k*ts[i]);
	  G[idx(ys[i], k, 1)].EB(idx(xs[i], (A*k+B)%C, 1), k*ts[i]);
