using namespace std;

struct edge {
	int from;
	int to;
	long long int cost;
};

struct aa {
	int now;
	long long int time;
	int nowv;
	bool comen;
};
class Compare {
public:
	bool operator()(const aa&l, const aa&r) {
		return l.time> r.time;
	}
};	//aa?????????????????Â¶

int v0, A, B, C;
long long int solve(const vector<vector<edge>>&edges,const int start,const int goal) {
	priority_queue<aa, vector<aa>, Compare>que;
	vector<vector<vector<long long int>>>memo(edges.size(), vector<vector<long long int>>(C,vector<long long int>(2,1e18)));
	memo[0][v0][0] = 0;
	que.push(aa{ start,0,v0,false });
	while (!que.empty()) {
		auto atop(que.top());
		que.pop();
		const int now = atop.now;
		const long long int nowtime = atop.time;
		const int nowv = atop.nowv;
		const int nowcomen = atop.comen;
		if (now == goal&&nowcomen)return nowtime;
		for (auto e : edges[atop.now]) {
			const int next = e.to;
			const long long  int nexttime = nowtime + e.cost*nowv;
			const int nextv = (A*nowv + B) % C;
			const bool nextcomen = nowcomen | (next == edges.size() - 1);
			if (nexttime < memo[next][nextv][nextcomen]) {
				que.push(aa{ next,nexttime,nextv,nextcomen });
				memo[next][nextv][nextcomen] = nexttime;
			}
		}
	}
	return -1;
}

int main() {
	int N, M; cin >> N >> M;
	vector<vector<edge>>edges(N);
	for (int i = 0; i < M; ++i) {
		int x, y, t; cin >> x >> y >> t;
		if (x > y)swap(x, y);
		x--; y--;
		edges[x].push_back(edge{ x,y,t });
		edges[y].push_back(edge{ y,x,t });
	}
	cin >> v0 >> A >> B >> C;
	long long int ans(solve(edges, 0, 0));
	cout << ans << endl;
	return 0;
}

