    for(int i=1;i<=N;i++) for(int j=0;j<C;j++){
        dp[i][j][0] = inf;
        dp[i][j][1] = inf;
    }
    dp[1][v0][0] = 0;
    priority_queue<state> Q;
    Q.push({1,0,v0,0});
    while(!Q.empty()){
        state now = Q.top(); Q.pop();
        if(dp[now.node][now.velocity][now.go]<now.dist) continue;
        for(auto x:v[now.node]){
            state ne = now;
            ne.velocity = next_v(now.velocity);
            ll d = x.dist*now.velocity;
            ne.node = x.to;
            if(now.node==N) ne.go = true;
            if(dp[x.to][ne.velocity][ne.go]>now.dist+d){
                dp[x.to][ne.velocity][ne.go] = now.dist+d;
                ne.dist = now.dist+d;
                Q.push(ne);
            }
        }
    }
    ll ans = inf;
    for(int i=0;i<C;i++) ans = min(ans,dp[1][i][1]);
    cout << ans << endl;
    return 0;
}

