  while(!q.empty()){
    P1 temp=q.top(); q.pop();
    ll cost=temp.first;
    ll V=temp.second.first;
    ll node=temp.second.second;
    if(d[V][node]<cost)continue;
    for(int i=0;i<G[node].size();i++){
