template <class T> void chmin(T &a, const T &b) {
  if (b < a)
    a = b;
}

struct edge {
  usize to;
  u64 cost;

  bool operator<(const edge &r) const { return r.cost < cost; }
};

static constexpr u64 inf = std::numeric_limits<u64>::max();

std::vector<u64> dijkstra(const std::vector<std::vector<edge>> &g,
                          const usize s) {
  const usize n = g.size();
  std::vector<u64> dist(n, inf);

  dist[s] = 0;
  std::priority_queue<edge> que;
  que.push({s, 0});

  while (!que.empty()) {
    const usize v = que.top().to;
    const usize c = que.top().cost;
    que.pop();
    if (dist[v] < c) {
      continue;
    }
    for (const auto &e : g[v]) {
      if (dist[e.to] > c + e.cost) {
        dist[e.to] = c + e.cost;
        que.push({e.to, dist[e.to]});
      }
    }
  }

  return dist;
}

