  const auto idx = [&](const usize r, const usize ve, const usize i) {
    return (r * c + ve) * n + i;
  };

  std::vector<std::vector<edge>> g(2 * c * n);

  const auto span = [&](const usize x, const usize y, const u64 t) {
    for (const auto r : {0, 1}) {
      for (usize i = 0; i != c; ++i) {
        g[idx(r, i, x)].push_back({idx(r, (a * i + b) % c, y), t * i});
      }
    }
  };

  for (const auto &e : es) {
    span(e.x, e.y, e.t);
    span(e.y, e.x, e.t);
  }

  for (usize i = 0; i != c; ++i) {
    g[idx(0, i, n - 1)].push_back({idx(1, i, n - 1), 0});
  }

  u64 ans = inf;
  const auto dist = dijkstra(g, idx(0, v0, 0));

  for (usize i = 0; i != c; ++i) {
    chmin(ans, dist[idx(1, i, 0)]);
  }

  std::cout << ans << std::endl;

  return 0;
}

