int dijkstra(int start) {
	rep(i, n) rep(j, c) rep(k, 2) {
		d[i][j][k] = INFL;
	}
	d[0][0][0] = 0;
	priority_queue<Vertex, vector<Vertex>, greater<Vertex>> q;
	for (q.push(Vertex(start, 0, v0, 0, false)); !q.empty(); ) {
		Vertex p = q.top(); q.pop();
		for (auto next_p : g[p.id]) {
			next_p.v = (a * p.v + b) % c;
			next_p.dist = p.dist + p.v * next_p.weight;
			next_p.flag = (p.flag || next_p.id == n - 1);
			if (d[next_p.id][next_p.v][next_p.flag] > next_p.dist) {
				d[next_p.id][next_p.v][next_p.flag] = next_p.dist;
				q.push(next_p);
