            G[c*y[i]+v].pb({c*x[i]+nv,t[i]*v});
            int A = c*n;
            G[A+c*x[i]+v].pb({A+c*y[i]+nv,t[i]*v});
            G[A+c*y[i]+v].pb({A+c*x[i]+nv,t[i]*v});
        }
    }

    rep(i,c)
    {
        int u = c*(n-1)+i, v = n*c+u;
