    using State = pair<int, Vertex>;
    priority_queue<State, vector<State>, greater<State>> pq; // cost, vertex
    vvvi d(n, vvi(c + 1, vi(2, inf)));
    d[0][v0][0] = 0;
    pq.push(State(0, make_tuple(0, v0, 0)));

    while (!pq.empty()) {
        int cost;
        Vertex vertex;
        tie(cost, vertex) = pq.top(); pq.pop();
        int i, j, k;
