struct State{
  ll dis, u, v, flag;
  State(ll a, ll b, ll c, ll d):dis(a), u(b), v(c), flag(d) {}
  bool operator<(const State &a) const {
    return dis > a.dis;
  }
};

int main(){
  ll n, m;
  cin >> n >> m;
  vector< vector<pll> > g(n);
  for(int i=0;i<m;i++){
    int x,y,t;
    cin >> x >> y >> t; x--; y--;
    g[x].emplace_back( make_pair(t,y) );
    g[y].emplace_back( make_pair(t,x) );
  }
  ll v,a,b,c;
  cin >> v >> a >> b >> c;

  vector< vector< vector<ll> > > d(n, vector< vector<ll> >(c, vector<ll>(2,INF)));
  assert(v<c);
  d[0][v][0] = 0;
  priority_queue<State> q;
  q.emplace( State(0,0,v,0) );

  while(not q.empty()){
    State state = q.top(); q.pop();
    ll dis = state.dis, u = state.u, v = state.v, flag = state.flag;
    if(d[u][v][flag] < dis) continue;

    ll nv = (a*v + b) % c;
    for(auto e : g[u]){
      ll ndis = dis + v*e.first, nu = e.second, nflag = (nu==n-1) ? 1 : flag;
