  for(int i=0;i<n;i++)
    for(int j=0;j<n;j++)
      e[i][j]=INF;
  for(int i=0;i<m;i++)
    e[x[i]-1][y[i]-1]=e[y[i]-1][x[i]-1]=t[i];

