}
void add_edge2(int time,int from,int to,int cost){
  G[time][from].push_back(edge(time,to,cost));
  G[time][to].push_back(edge(time,from,cost));
}
struct st{
  int dist,time,pos;
  st(){}
  st(int dist,int time,int pos):dist(dist),time(time),pos(pos){}
  bool operator<(const st& a) const{
    return dist>a.dist;
  }
};
