ll dijkstra(int start,int goal,int c){
  ll D[500][1001];
  for(int i=0;i<500;i++)
    for(int j=0;j<1001;j++) D[i][j] = INF;

  priority_queue<PP,vector<PP>,greater<PP> > Q;
  Q.push(PP(0,P(start,c)));
  D[start][c] = 0;
  ll res = INF;
  while(!Q.empty()){
    PP t = Q.top();Q.pop();
    ll cost = t.first; 
    int pos = t.second.first;
    int cnt = t.second.second;
    if(D[pos][cnt]<cost) continue;
    if(pos==goal&&goal == 0)return cost; 
    if(pos==goal&&goal == n-1){
      res=min(res,cost+dijkstra(n-1,0,cnt));
      continue;
    }
    for(int i=0;i<(int)G[pos].size();i++){
      int npos = G[pos][i].first;
      ll ncost = cost+v[cnt]*G[pos][i].second;
      int ncnt = (cnt+1)%C;
      if(D[npos][ncnt]<=ncost)continue;
      Q.push(PP(ncost,P(npos,ncnt)));
      D[npos][ncnt] = ncost;
    }
  }
  return res;
}

