signed main() {
	INIT;
	VAR(int, n, m);
	VEC_ROW(int, m, x, y, t);
	VAR(int, v0, a, b, c);

	struct Edge {
		int to, cost;
		Edge() {}
		Edge(int to, int cost) : to(to), cost(cost) {}
		bool operator<(const Edge& r) const {
			return cost < r.cost;
		}
	};
	std::vector<std::vector<Edge>> g(n+n);
	// ????????????
	REP(i, m) {
		--x[i]; --y[i];
		g[x[i]].emplace_back(y[i], t[i]);
		g[y[i]].emplace_back(x[i], t[i]);
		int xx = x[i] + n;
		int yy = y[i] + n;
		if (xx == n - 1 + n) xx = n - 1;
		if (yy == n - 1 + n) yy = n - 1;
		g[xx].emplace_back(yy, t[i]);
		g[yy].emplace_back(xx, t[i]);
	}
	//
	std::map<PAIR, int> dist;
	std::function<void(PAIR)> Dijkstra = [&](PAIR s) {
		using P = std::pair<int, PAIR>;
		std::priority_queue<P, std::vector<P>, std::greater<P>> pq;
		dist.clear();
		dist[s] = 0;
		pq.push(P(0, s));
		while (!pq.empty()) {
			P p = pq.top(); pq.pop();
			PAIR v = p.second;
			if (dist[v] < p.first) continue;
			for (int i = 0; i < g[v.first].size(); ++i) {
				Edge e = g[v.first][i];
				int vne = (a*v.second + b) % c;
				PAIR to = PAIR(e.to, vne);
				if (dist.find(to) == dist.end()) dist[to] = INFLL;
				int cost = v.second*e.cost;
				if (dist[to] > dist[v] + cost) {
					dist[to] = dist[v] + cost;
					pq.push(P(dist[to], to));
				}
			}
		}
	};
	Dijkstra(PAIR(0, v0));
	int ans = INFLL;
	REP(i, c) {
		if (dist.find(PAIR(n, i)) == dist.end()) continue;
		CHMIN(ans, dist[PAIR(n, i)]);
	}
	OUT(ans)BR;
	return 0;
}

