	std::vector<int> diN(c, INFLL);
	REP(i, c) {
		if (dist.find(PAIR(n - 1, i)) == dist.end()) continue;
		diN[i] = dist[PAIR(n - 1, i)];
	}
	int ans = INFLL;
	REP(i, c) {
		if (diN[i] == INFLL) continue;
		Dijkstra(PAIR(n - 1, i));
		REP(j, c) {
			if (dist.find(PAIR(0, j)) == dist.end()) continue;
			CHMIN(ans, diN[i] + dist[PAIR(0, j)]);
		}
	}
	OUT(ans)BR;
	return 0;
}

