ll dijkstra(){
	memset(dp,-1,sizeof(dp));
	priority_queue<data> que;
	que.push(data(1,v0,0,0));
	dp[1][v0][0]=0;
	while(que.size()){
		data q=que.top();
		que.pop();
		if(dp[q.v][q.sp][q.f]<q.c)continue;
		if(q.v==n)q.f=1;
		if(q.v==1 && q.f==1)return q.c;
		for(int i=0;i<G[q.v].size();i++){
			int nv=(a*q.sp+b)%c;
			if(dp[G[q.v][i].first][nv][q.f]>q.c+q.sp*G[q.v][i].second){
				dp[G[q.v][i].first][nv][q.f]=q.c+q.sp*G[q.v][i].second;
				que.push(data(G[q.v][i].first,nv,dp[G[q.v][i].first][nv][q.f],q.f));
