#include <bits/stdc++.h>
#define N 501
using namespace std;
typedef long long ll;
typedef pair<ll,ll> P;
typedef pair<ll,P> PP;
ll const INF = 1LL<<60;
ll n,m;
vector<P> G[N];
ll v0,a,b,C;
ll v[5010];

ll nC;
ll dijkstra(ll start,ll goal,ll c){
  ll D[500][1001];
  for(ll i=0;i<500;i++)
    for(ll j=0;j<1001;j++) D[i][j] = INF;

  priority_queue<PP,vector<PP>,greater<PP> > Q;
  Q.push(PP(0,P(start,c)));
  D[start][c] = 0;
  ll res = INF;
  while(!Q.empty()){
    PP t = Q.top();Q.pop();
    ll cost = t.first; 
    ll pos = t.second.first;
    ll cnt = t.second.second;
    if(D[pos][cnt]<cost) continue;
    if(pos==goal&&goal == 0)return cost; 
    if(pos==goal&&goal == n-1){
      res=min(res,cost+dijkstra(n-1,0,cnt));
      continue;
    }
    for(ll i=0;i<(ll)G[pos].size();i++){
      ll npos = G[pos][i].first;
      ll ncost= cost+v[cnt]*G[pos][i].second;
      ll ncnt = (cnt+1)%nC;
      if(D[npos][ncnt]<=ncost)continue;
      Q.push(PP(ncost,P(npos,ncnt)));
      D[npos][ncnt] = ncost;
    }
  }
  return res;
}

int main(){
  cin>>n>>m;
  for(ll i=0,x,y,t;i<m;i++){
    cin>>x>>y>>t;
    x--,y--;
    G[x].push_back(P(y,t));
    G[y].push_back(P(x,t));
  }
  cin>>v[0]>>a>>b>>C;
  set<ll> S;
  S.insert(v[0]);
  for(ll i=1;;i++){
    v[i] = (a*v[i-1]+b)%C;
    if(S.count(v[i]))break;
    S.insert(v[i]);
  }
  nC=S.size();
  cout<<dijkstra(0,n-1,0)<<endl;
  return 0;
}

