vector<ll> dijkstra(int s,int n,Graph g){
    typedef pair<ll,ll> P;
    vector<ll> d(n,LNF);
    priority_queue<P,vector<P>,greater<P>> que;
    d[s] = 0;
    que.push(P(0,s));
    while(!que.empty()){
        P p = que.top();
        que.pop();
        int v = p.second;
        if(d[v]<p.first) continue;
        rep(i,0,g[v].size()){
            Edge e = g[v][i];
            if(d[e.to]>d[v]+e.weight){
                d[e.to] = d[v] + e.weight;
                que.push(P(d[e.to],e.to));
            }
        }
    }
    return d;
}

