            long long next_cost = now_cost + adjlist[now_n][i].first * now_v;
            bool next_isarrive = (now_isarrive || (next_n == n - 1));

            if(result[next_v][next_isarrive][next_n] > next_cost){
                result[next_v][next_isarrive][next_n] = next_cost;
                node next_node;
                next_node.cost = -next_cost;
                next_node.n = next_n;
                next_node.v = next_v;
                next_node.isarrive = next_isarrive;
                wait.push(next_node);
            } 
        }
    }

    long long ans = INF;
    for(int i = 0; i < c; i++){
        ans = min(ans, result[i][1][0]);
    }
    cout << ans << endl;
    return 0;
}

