#include <bits/stdc++.h>
#define EPS 1e-8
using namespace std;
typedef long long ll;
typedef pair<int,int> P;

int n;
double k;
double s[100001];
long double sum[100001];

class segtree{
public:
	static const int N=1<<18;
	int dp[1<<19];
	segtree(){
		memset(dp,0,sizeof(dp));
	}
	void update(int k,int v){
		k+=N-1;
		dp[k]=v;
		while(k>0){
			k=(k-1)/2;
			dp[k]=min(dp[k*2+1],dp[k*2+2]);
		}
	}

	int query(int a,int b,int k=0,int l=0,int r=N){
		if(b<=l || r<=a)return N;
		if(a<=l && r<=b)return dp[k];
		int mid=(l+r)/2;
		int vl=query(a,b,k*2+1,l,mid);
		int vr=query(a,b,k*2+2,mid,r);
		return min(vl,vr);
	}
};

segtree seg;

int main(void){
	scanf("%d%lf",&n,&k);
	for(int i=0;i<n;i++){
		scanf("%lf",&s[i]);
	}
	if(k<EPS){
		int len=0;
		int res=0;
		for(int i=0;i<n;i++){
			if(s[i]<EPS)len++;
			else len=0;
			if(len>0)res=n;
		}
		printf("%d\n",res);
		return 0;
	}
	for(int i=0;i<n;i++){
		if(s[i]<EPS){
			printf("%d\n",n);
			return 0;
		}
	}
	vector<long double> vec;
	vec.push_back(0.0);
	for(int i=0;i<n;i++){
		sum[i+1]+=sum[i];
		sum[i+1]+=logl(s[i]);
		vec.push_back(sum[i+1]);
	}
	sort(vec.begin(),vec.end());
	vec.erase(unique(vec.begin(),vec.end()),vec.end());
	for(int i=0;i<vec.size();i++){
		seg.update(i,1<<18);
	}
	int ans=0;
	for(int i=0;i<n;i++){
		int v=lower_bound(vec.begin(),vec.end(),sum[i])-vec.begin();
		seg.update(v,i);
		v=lower_bound(vec.begin(),vec.end(),sum[i+1]-logl(k))-vec.begin();
		ans=max(ans,i+1-seg.query(v,vec.size()));
	}
	printf("%d\n",ans);
	return 0;
}

