    // 0がある
    bool is_zero = false;
    // すべてk以上
    bool over_k = true;
    rep(i, n) {
        if (s[i] == 0.0) is_zero = true;
        if (s[i] <= k) over_k = false;
    }

    if (is_zero) {
        cout << n << endl;
        return 0;
    }
