    vector<double> sum(n + 1, 0.0);
    rep(i, n) {
        sum[i + 1] = sum[i] + s[i];
    }

    auto copy = sum;

    for (int i = n - 1; i >= 0; --i) {
        chmin(copy[i], copy[i + 1]);
    }

    int ans = 0;
