bool operator<(const vector<long long int>&l, const vector<long long int>&r) {
	if(l.size()<r.size())return true;
	else if(l.size()>r.size())return false;

	for (int i = 0; i < l.size(); ++i) {
		if (l[l.size() - i - 1] > r[l.size() - i - 1]) {
			return false;
		}
		else if (l[l.size() - i - 1] < r[l.size() - i - 1]) {
			return true;
		}
	}
	return false;
}

