int main(void) {
  int i, j, n, h, t;
  double k;
  scanf("%d%lf", &n, &k);
  h = n + 1, t = 0;
  double s[n];
  for(i = 0; i < n; ++i) scanf("%lf", &s[i]);
  while( 1 ) {
    int now = (h + t) / 2;
    int st = 1;
    double tmp = 1;
    for(i = 0; i < now; ++i) tmp *= s[i];
    if(tmp <= k) {
      t = now;
      if(h - t == 1) break;
      else continue;
    }
    for(i = now; i < n; ++i) {
      tmp /= s[i - now];
      tmp *= s[i];
      if(tmp <= k) {
        t = now;
        st = 0;
        break;
      }
    }
    if(!st) {
      if(h - t == 1) break;
      else continue;
    }
    else h = now;
  }
  printf("%d\n", t);
  return 0;
