    L[i+1] += L[i];
  st.build(L);
  int ans = 0;
  for(int i = 0; i < n; ++i){
    int ok = i, ng = n+2;
    while(ng-ok > 1){
      int m = (ok+ng)/2;
      double mi = st.query(m,n+1);
      if(mi-L[i] > lk) ng = m;
      else ok = m;
    }
    ans = max(ans,ok-i);
  }
  cout << ans << endl;
}

