bool C(int v){
	double now=0;
	for(int i=0;i<v;i++){
		if(s[i]<EPS)return true;
		now+=log(s[i]);
	}
	if(now<=log(k))return true;
	for(int i=v;i<n;i++){
		if(s[i]<EPS)return true;
		now+=log(s[i]);
