	  bool[] pass = new bool[n+1];
	  for(int l=1;l<=n; l++){
		pass[l] = false;
		for(int i=0;i<=(n-l); i++){
			if(l==1) {
				ra[i] = a[i];
			} else {
				ra[i] = rb[i]*a[l+i-1];
			}
			if(ra[i] < k) pass[l] = true;
		}
		rb = ra;
	  }

	  int ret = 0;
