  public static int calc2(int n,decimal k,decimal[] a) {
	  decimal[] b = new decimal[n];
	  for(int i=0; i<n; i++) if(a[i]<0.005m) { return n; } else { b[i] = (decimal)a[i]; }
	  decimal lk = (decimal)(k+0.005m);
//	  for(int i=0; i<n; i++) if(a[i]<0.005m) { return n; } else { b[i] = (decimal)Math.Log(a[i]); }
//	  decimal lk = (decimal)Math.Log(k+0.005m);

	  decimal[] ra = new decimal[n];
	  decimal[] rb = new decimal[n];
	  rb[0] = 1m;
	  for(int i=0; i<n; i++) rb[0] *= b[i];
//	  rb[0] = 0m;
//	  for(int i=0; i<n; i++) rb[0] += b[i];
	  if(rb[0] < lk) return n;
	  for(int l=n-1;0<l; l--){
		for(int i=0;i<(n-l); i++){
			ra[i] = rb[i]/b[l+i];
//			ra[i] = rb[i]-b[l+i];
			if(ra[i] < lk) return l;
		}
		ra[n-l] = rb[n-l-1]/b[n-l-1];
//		ra[n-l] = rb[n-l-1]-b[n-l-1];
		if(ra[n-l] < lk) return l;
		rb = ra;
	  }

	  return 0;
  }

  public static int calc3(int n,double k,double[] a) {
	  double prod = 1.0;
	  for(int i=0; i<n; i++) prod *= a[i];
	  double[] lp = new double[n+1];
	  for(int i=0; i<=n; i++) lp[i] = (i==0) ? 1.0 : (lp[i-1]*a[i-1]);
	  double[] rp = new double[n+1];
	  for(int i=0; i<=n; i++) rp[i] = (i==0) ? 1.0 : (rp[i-1]*a[n-i]);

	  for(int l=n;0<l; l--){
		for(int i=0;i<=(n-l); i++){
			double v = prod / lp[i] / rp[n-l-i];
			if(v < k) return l;
		}
	  }

	  return 0;
  }



