  public static int calc3(int n,double k,double[] a) {
	  double[] lp = new double[n+1];
	  double[] rp = new double[n+1];
	  for(int i=0; i<=n; i++) {
		if((i<n)&&(a[i]<0.005)) return n;
//		lp[i] = (i==0) ? 1.0 : (lp[i-1]*a[i-1]);
//		rp[i] = (i==0) ? 1.0 : (rp[i-1]*a[n-i]);
