		rp[i] = (i==0) ? 0.0 : (rp[i-1]+Math.Log(a[n-i]));
	  }
	  if(k<0.005) return 0;
//	  double prod = lp[n]/(k+0.005);
	  double prod = lp[n]-Math.Log(k+0.005);

	  double[] lp_max = new double[n+1];
	  double[] rp_max = new double[n+1];
	  double[] lp_min = new double[n+1];
	  double[] rp_min = new double[n+1];
