			lp_max[0] = lp[0];
			rp_max[0] = rp[0];
			lp_min[0] = lp[0];
			rp_min[0] = rp[0];
		} else {
			lp_max[i] = (lp_max[i-1]<lp[i])? lp[i] : lp_max[i-1];
			rp_max[i] = (rp_max[i-1]<rp[i])? rp[i] : rp_max[i-1];
			lp_min[i] = (lp_min[i-1]>lp[i])? lp[i] : lp_min[i-1];
			rp_min[i] = (rp_min[i-1]>rp[i])? rp[i] : rp_min[i-1];
