	  int imin = 0;
//	  int imax = n;
	  for(int i=0; i<=n; i++) {
		if(prod < (lp_max[i]+rp_max[i])) {
			nmax = n-i;
			break;
		}
	  }
	  for(int i=0; i<=n; i++) {
		if(prod < (lp[i]+rp_max[n-i])) {
			imin = i;
			break;
		}
	  }

	  for(int l=nmax;0<l; l--){
		int q = n-l;
		for(int i=imin;i<=q; i++){
//			if(prod < (lp[i]*rp[q-i])) return l;
			if(prod < (lp[i]+rp[q-i])) return l;
		}
	  }

	  return 0;
  }



