using namespace std;


struct Info{
	Info(ll arg_minus_value,ll arg_loc){
		minus_value = arg_minus_value;
		loc = arg_loc;
	}
	ll minus_value,loc;
};


int main(){

	ll S,T,D;
	scanf("%lld %lld %lld",&S,&T,&D);

	ll* table = new ll[D];
	for(int i = 0; i < D; i++)scanf("%lld",&table[i]);

	ll min_maximum = 0;

	vector<Info> V;
	if(table[0] < 0){
		V.push_back(Info(abs(table[0]),0));
		min_maximum = max(min_maximum,abs(table[0]));
	}

	for(int i = 1; i <= D-1; i++){
		table[i] += table[i-1];
		if(table[i] < 0){
			V.push_back(Info(abs(table[i]),i));
			min_maximum = max(min_maximum,abs(table[i]));
		}
	}

	ll tmp = S;

	for(int day = 1; day <= D; day++){
		tmp = S+table[day-1];
		if(tmp <= T){
			printf("%d\n",day);
			return 0;
		}
	}

	if(tmp >= S){
		printf("-1\n");
		return 0;
	}

	ll ans = D;

	while(tmp-min_maximum > T){
		ans += D;
		tmp -= V[V.size()-1].minus_value;
	}

	ll pre_loc = -1;

	for(int i = 0; i < V.size(); i++){

		if(V[i].loc <= pre_loc){
			ans += (V[i].loc+D)-pre_loc;
		}else{
			ans += V[i].loc-pre_loc;
		}
		if(tmp -V[i].minus_value<= T){
			printf("%lld\n",ans);
			return 0;
		}
		pre_loc = V[i].loc;
	}
	return 0;
}

