template<class T>class CulSum {
	size_t n; vector<T> s;
public:
	CulSum(const vector<T>& a) :n(a.size()), s(n + 1) {
		for (size_t i = 0; i < n; ++i)s[i + 1] = s[i] + a[i];
	}
	template<class U, class F>CulSum(const U& a, F f) : n(a.size()), s(n + 1) {
		for (int i = 0; i < n; ++i)s[i + 1] = s[i] + static_cast<T>(f(a[i]));
	}
	// [l, r)
	T operator()(size_t l, size_t r)const {
		assert(l < r && r <= n);
		return l > r ? 0 : s[r] - s[l];
	}
	// [0, r)
	T operator()(size_t r)const {
		assert(r <= n);
		return s[r];
	}
	const vector<T>& operator*()const { return s; }
};

int main() {
	ll S, T; int n;
	in(S, T, n);
	VL a = in[n];
	ll x = T - S;
	CulSum<ll> s(a);
	ll s_min = Min(*s);

	if (s(n) >= 0) {
		rep(i, n) {
			if (s(i + 1) <= x)
				out.exit(i + 1);
		}
		out.exit(-1);
	}

	auto calc_min = [&](ll d) {
		if (d == 0) {
			return 0ll;
		} else {
			return s(n) * (d - 1) + s_min;
		}
	};

	ll ok = 0, ng = 10000000;
	while (abs(ok - ng) > 1) {
		ll mid = Mid(ok, ng);
		(calc_min(mid) > x ? ok : ng) = mid;
	}
	rep(i, n) {
		if (s(n) * ok + s(i + 1) <= x)
			out.exit(ok * n + i + 1);
	}
}

