    W.resize(D);
    for(auto &e : W) cin >> e;
    V<ll> sum(D + 1);
    for(ll i = 0; i < D; i++) sum[i + 1] = sum[i] + W[i];
    auto min_v = min_element(sum.begin(), sum.end());
    if(*min_v == 0) return -1;
    if(S + *min_v > T && sum.back() >= 0) return -1;
    ll diff = T - S;
