    ll x = max<ll>(0, S - init_ok);
    ll y = abs(sum.back());
    ll ret = 0;
    if(y) ret += x / y + !!(x % y);
    ll now = S - abs(sum.back()) * ret;
    ret *= D;
    for(ll i = 0; i < D; i++) {
        now += W[i];
        if(now <= T) return ret + i + 1;
    }
    assert(false);
}

