void solve(){
	int s, t, d;
	cin >> s >> t >> d;
	int req_loss = s - t;

	vector<int> inputs;
	int summing = 0;
	int going = 0;
	REP(i, d) {
		int a;
		cin >> a;
		a *= -1LL;
		summing += a;
		going = min(going, summing);
		inputs.push_back(a);
	}
	int ans = 0;
	REP(i, 30LL * d) {
		ans++;
		req_loss -= inputs[i % d];
		if (req_loss <= 0) {
			cout << ans << endl;
			return;
		}
	}
	ll bot = 1e5;
	ll top = -1;
	while (bot - top > 1) {
		ll mid = (top + bot) / 2LL;
		int now_val = req_loss - mid * summing;
		REP(j, d) {
			now_val -= inputs[j];
			if (now_val <= 0) break;
		}
		if (now_val <= 0) {
			bot = mid;
		}
		else {
			top = mid;
		}
	}
	ans += bot * d;
	req_loss -= bot * summing;
	REP(i, 30LL * d) {
		ans++;
		req_loss -= inputs[i % d];
		if (req_loss <= 0) {
			cout << ans << endl;
			return;
		}
	}
	cout << -1 << endl;
}

