struct Info{
	Info(int arg_minus_value,int arg_loc){
		minus_value = arg_minus_value;
		loc = arg_loc;
	}
	int minus_value,loc;
};


int main(){

	int S,T,D;
	scanf("%d %d %d",&S,&T,&D);

	int* table = new int[D];
	for(int i = 0; i < D; i++)scanf("%d",&table[i]);

	vector<Info> V;
	if(table[0] < 0){
		V.push_back(Info(abs(table[0]),0));
	}

	for(int i = 1; i <= D-1; i++){
		table[i] += table[i-1];
		if(table[i] < 0){
			V.push_back(Info(abs(table[i]),i));
		}
	}

	int tmp = S;

	for(int day = 1; day <= D; day++){
		tmp = S+table[day-1];
		if(tmp <= T){
			printf("%d\n",day);
			return 0;
		}
	}

	if(tmp >= S){
		printf("-1\n");
		return 0;
	}

	int pre_loc = -1;
	ll ans = D;

	while(true){

		for(int i = 0; i < V.size(); i++){

			tmp -= V[i].minus_value;

			if(V[i].loc <= pre_loc){
				ans += (V[i].loc+D)-pre_loc;
			}else{
				ans += V[i].loc-pre_loc;
			}
			if(tmp <= T){
				printf("%d\n",ans);
				return 0;
			}
			pre_loc = V[i].loc;
		}
	}

	return 0;
}

